import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./UserForm.css";

function UserForm() {

  // React States
  const [errorMessages, setErrorMessages] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);


  const handleResponse = (response) => {
    if (response.status === 200) {
      }
      else {
        // Error
      }
  }

  const handleError = (error) => {
    console.log(error);
  }

  const handleSubmit = (event) => {
    event.preventDefault();

    const formData = event.target.elements;
    const data = {
      "username": formData[0].value,
      "password": formData[1].value
    };

    console.log(data);

    const requestOptions = {
      method: "POST",
      credentials: 'include',
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data),
    };
    fetch("http://localhost:9001/v1/users/login", requestOptions)
      .then((response) => handleResponse(response))
      .catch((error) => handleError(error));
  };


  // JSX code for login form
  const renderForm = (
    <div className="form">
      <form onSubmit={handleSubmit}>
        <div className="input-container">
          <label>Bed Number </label>
          <input type="text" name="bed" required />
        </div>
        <div className="input-container">
          <label>Bath Number </label>
          <input type="password" name="bath" required />
        </div>
        <div className="input-container">
          <label>House Size </label>
          <input type="text" name="house" required />
        </div>
        <div className="input-container">
          <label>Zip Code </label>
          <input type="text" name="zip" required />
        </div>
        <div className="button-container">
          <input type="submit" />
        </div>
      </form>
    </div>
  );

  return (
    <div className="app">
      <div className="user-form">
        <div className="title">Enter The House Information For A Prediction</div>
        {renderForm}
      </div>
    </div>
  );
}

export default UserForm;
